import React from "react";

const MenuCard = ({ menuData }) => {
  console.log(menuData);
  return (
    <>
    <section className="main-card--cointainer">
      {menuData.map((cur) => {
        console.log(cur);
        return (
          <>
            
              <div className="card-container">
                <div className="card">
                  <div className="card-body">
                    <span className="card-number card-circle subtle">1</span>
                    <span className="card-author subtle">Breakfast</span>
                    <h2 className="card-title">Maggie</h2>
                    <span className="card-description subtle">
                      How do you describe Maggi? Image result for information of
                      maggi noodles Maggi noodles are dried noodles fused with
                      oil, and sold with a packet of flavorings. These noodles
                      are usually eaten after being cooked in boiling water for
                      3 to 5 minutes or eaten straight from the packet. Just
                      boil the water, add the tastemaker and noodles and your
                      maggi is ready within 2 to 3 minutes.
                    </span>
                    <div className="card-read">Read More</div>
                  </div>
                  {/* <img src={image} alt="image" className="card-media" /> */}
                  <span className="card-tag subtle">Order Now</span>
                </div>
              </div>
          </>
        );
      })}
      </section>
    </>
  );
};

export default MenuCard;
